components.onSaveSuccess:
alert("Your Enrolment is complete", {type:"success", flash:true}); 
window.params.saved=true;
$("#pbid-ConfirmEnrolmentComplete").addClass("disabled");

components.onClick:
var responsesLength=$SelectedResponses.$data.length;
var count=0;
for (var k=0;k<responsesLength;k++)
{
if ($SelectedResponses.$data[k].STATUS_NAME ===  'Completed')
{
count++;
}
}
if (responsesLength===count){
$ConfirmEnrolmentConfirmation.$save()
}
else{
alert("Please complete all action items before confirming your Enrolment", {type:"error", flash:true}); 
}
