this.AIPGetControllerId = function (id) {
var rootScope = angular.element(document.querySelector('#pbid-ContactInformation')).injector().get('\$rootScope');

     var scope = rootScope.\$\$childHead;
    //traverse children of root scope to find the id matching _controllerId
    for (; scope && scope._controllerId !== id; scope = scope.\$\$nextSibling){}
    return scope;
};

this.AIPCreateNewRecord = function (detailName) {
   setTimeout(function () {
       var scope = AIPGetControllerId(controllerId);
       console.log("scope[detailName + 'DS'] : " + scope[detailName + 'DS']);
       if (scope[detailName + 'DS'].data.length === 0) {
           scope[detailName + 'DS'].add(scope['pbNew_' + detailName]());
           scope[detailName + 'DS'].setCurrentRecord(scope[detailName + 'DS'].data[0]);
           scope.$apply();
       }
   });
};
