components.onLoad:
var eligibility= $EcafEligibilityDetail.ELIGIBILITY;

console.debug("The current user's ECAF eligibility categories are: " + eligibility);

if (eligibility) {    
    // Show the intro text
    $IntroTextWithOptionsBlock.$visible=true;

    if(eligibility.includes('H')) {        
        // The user is eligible for HECS HELP, let's show that option
        console.debug("Showing the HECS-HELP block.");
        $HECSOptionBlock.$visible=true;
    }
    if(eligibility.includes('F')) {        
        // The user is eligible for FEE HELP, let's show that option
        console.debug("Showing the FEE-HELP block.");
        $FEEOptionBlock.$visible=true;
    }
    if(eligibility.includes('O')) {        
        // The user is eligible for OSHELP, let's show that option
        console.debug("Showing the OS-HELP block.");
        $OSOptionBlock.$visible=true;
    }
    if(eligibility.includes('S')) {        
        // The user is eligible for SA HELP, let's show that option
        console.debug("Showing the SA-HELP block.");
        $SAOptionBlock.$visible=true;
    }
}
else {
    // Show the intro text
    $IntroTextWithNoOptionsBlock.$visible=true;
}

