components.onUpdate:
$TFN = $("#pbid-TFNTextHECS-0").val();

//remove spaces and update
tfn = $TFN.replace(/\s+/g, '');
$('#pbid-TFNTextHECS-0').val(tfn);

//remove hyphens and update
tfn = $TFN.replace(/[-]/g, '');
$('#pbid-TFNTextHECS-0').val(tfn);

//validate only digits
var isNumber = /^[0-9]+$/.test(tfn);


if(!isNumber) {
    document.getElementById('pbid-TFNValidationHECS-label-0').innerHTML = "<span style='color: red;'>Invalid TFN! Only numbers are allowed.</span>";

}

else {
		var length =  $TFN.length;
		if(length != 9) {
            document.getElementById('pbid-TFNValidationHECS-label-0').innerHTML = "<span style='color: orange;'>Invalid TFN! Must have 9 digits.</span>";
		}
		else {
            var digits =  $TFN.split('');
			var sum = (digits[0]*1)
			+ (digits[1]*4)
			+ (digits[2]*3)
			+ (digits[3]*7)
			+ (digits[4]*5)
			+ (digits[5]*8)
			+ (digits[6]*6)
			+ (digits[7]*9)
			+ (digits[8]*10);

			var remainder = sum % 11;
				if(remainder == 0) {
                    document.getElementById('pbid-TFNValidationHECS-label-0').innerHTML = "<span style='color: green;'>Valid TFN.</span>";
				} else {
                    document.getElementById('pbid-TFNValidationHECS-label-0').innerHTML = "<span style='color: red;'>Not a valid TFN number!</span>";
				}
        }
}

